<? extract($object->navParams); ?><div>	<h2>Список видео (<?=$count?>)</h2>	<? if ($count < 1): ?>	<p>Нету ни одного видео</p>	<? else :?>	<table class="table table-hover table-bordered">	<thead>	<tr>		<th class="numberlist">№</th>		<th> Заголовок</th>		<th class="hidden-xs hidden-sm"> Просмотреть</th>		<th> Редактировать</th>		<th> Удалить</th>	</tr>	</thead>	<tbody>		<? 	$i = ($page_num - 1) * $on_page + 1; ?>		<? foreach ($this->video as $video): ?>			<tr>			<? $id = $video['fid'] ?>				<td><?=$i?></td>				<td><?=$video['title'] ?></td>				<td class="hidden-xs hidden-sm">					<div style = "width: 300px">						<video class="video-js" controls preload="auto" data-setup="{}">							<source src="<?=BASE_URL.VIDEO_DIR.$video['name']?>" type='video/mp4'>							<p class="vjs-no-js">								To view this video please enable JavaScript, and consider upgrading to a web browser that								<a href="http://videojs.com/html5-video-support/" target="_blank">supports HTML5 video</a>							</p>						</video>					</div>				</td>					<td>				<? if ($object->check_priv('C_Video:action_edit')) :?>					<a href="<?=M_Link::ToAdminVideo('edit',$id)?>"> Редактировать</a>				<? endif?>				</td>				<td>				<? if ($object->check_priv('C_Video:action_delete')) :?>					<a href="<?=M_Link::ToAdminVideo('delete',$id)?>" onClick="javascript: return confirm('Вы действительно хотите удалить?')"> Удалить</a>				<? endif?>				</td>			</tr>		<? $i++; endforeach ?>	</tbody>	</table>	<?=$object->navBar ?>	<?endif?>	<? if ($object->check_priv('C_Video:action_add')) :?>	<p><a class="btn btn-primary btn" href="<?=M_Link::ToAdminVideo('add')?>">Добавить новое видео &raquo;</a></p>	<? endif?></div>